CORNER_R  = 14
POLL_MS   = 1000   # опрашиваем winsdk раз в секунду (он сам кэширует)

GWL_EXSTYLE       = -20
WS_EX_TRANSPARENT = 0x00000020
WS_EX_LAYERED     = 0x00080000

def set_click_through(hwnd: int, on: bool):
    u32 = ctypes.windll.user32
    s = u32.GetWindowLongW(hwnd, GWL_EXSTYLE)
    u32.SetWindowLongW(hwnd, GWL_EXSTYLE,
        (s | WS_EX_TRANSPARENT | WS_EX_LAYERED) if on else (s & ~WS_EX_TRANSPARENT))

