def ms_to_mmss(ms: int) -> str:
    if ms <= 0: return "0:00"
    s = ms // 1000
    return f"{s//60}:{s%60:02d}"

def rounded_pixmap(px: QPixmap, r: int) -> QPixmap:
    out = QPixmap(px.size())
    out.fill(Qt.GlobalColor.transparent)
    p = QPainter(out)
    p.setRenderHint(QPainter.RenderHint.Antialiasing)
    path = QPainterPath()
    path.addRoundedRect(QRectF(px.rect()), r, r)
    p.setClipPath(path)
    p.drawPixmap(0, 0, px)
    p.end()
    return out

def bytes_to_pixmap(data: bytes, size: int) -> Optional[QPixmap]:
    if not data or len(data) < 64:
        return None
    img = QImage()
    if not img.loadFromData(data):
        return None
    px = QPixmap.fromImage(img).scaled(
        size, size,
        Qt.AspectRatioMode.KeepAspectRatioByExpanding,
        Qt.TransformationMode.SmoothTransformation,
    )
    if px.width() != size or px.height() != size:
        x = (px.width()  - size) // 2
        y = (px.height() - size) // 2
        px = px.copy(x, y, size, size)
    return rounded_pixmap(px, 10)

# Иконка встроена как base64 — не нужен внешний файл
_ICO_B64 = ""

def make_tray_icon(accent: str = "#E91E8C") -> QIcon:
    import base64 as _b64
    data = _b64.b64decode(_ICO_B64)
    px = QPixmap()
    px.loadFromData(data)
    if not px.isNull():
        return QIcon(px)
    # Fallback если декодирование не удалось
    px = QPixmap(64, 64)
    px.fill(Qt.GlobalColor.transparent)
    p = QPainter(px)
    p.setRenderHint(QPainter.RenderHint.Antialiasing)
    p.setBrush(QColor(accent))
    p.setPen(Qt.PenStyle.NoPen)
    p.drawEllipse(2, 2, 60, 60)
    p.setPen(QColor("white"))
    p.setFont(QFont("Segoe UI", 28, QFont.Weight.Bold))
    p.drawText(px.rect(), Qt.AlignmentFlag.AlignCenter, "♪")
    p.end()
    return QIcon(px)

def get_system_fonts() -> list[str]:
    all_fonts = QFontDatabase.families()
    preferred = ["Betm Rounded SemiBold", "Betm Rounded", "Betm"]
    result = []
    for p in preferred:
        for f in all_fonts:
            if p.lower() in f.lower() and f not in result:
                result.append(f)
    nice = ["Segoe UI", "Yu Gothic UI", "Calibri", "Verdana", "Trebuchet MS",
            "Century Gothic", "Franklin Gothic Medium", "Gill Sans MT"]
    for n in nice:
        if n in all_fonts and n not in result:
            result.append(n)
    for f in all_fonts:
        if f not in result:
            result.append(f)
    return result

