FALLBACK_COVER_URL = "https://avatars.steamstatic.com/fef49e7fa7e1997310d705b2a6158ff8dc1cdfeb_full.jpg"
_fallback_cover_cache: Optional[bytes] = None

def get_fallback_cover() -> Optional[bytes]:
    global _fallback_cover_cache
    if _fallback_cover_cache is not None:
        return _fallback_cover_cache
    try:
        import urllib.request
        req = urllib.request.Request(FALLBACK_COVER_URL, headers={"User-Agent": "SpotifyOverlay/1.0"})
        with urllib.request.urlopen(req, timeout=5) as r:
            _fallback_cover_cache = r.read()
        return _fallback_cover_cache
    except Exception:
        return None

