# Прогресс-бар
# ─────────────────────────────────────────────────────────────────────────────
class ProgressBar(QWidget):
    def __init__(self, parent=None):
        super().__init__(parent)
        self.setFixedHeight(3)
        self._v      = 0.0
        self._accent = "#E91E8C"

    def set_accent(self, color: str):
        self._accent = color
        self.update()

    def set_progress(self, v: float):
        self._v = max(0.0, min(1.0, v))
        self.update()

    def paintEvent(self, _):
        p = QPainter(self)
        p.setRenderHint(QPainter.RenderHint.Antialiasing)
        w, h = self.width(), self.height()
        r = h / 2
        p.setBrush(QColor(50, 50, 50))
        p.setPen(Qt.PenStyle.NoPen)
        p.drawRoundedRect(0, 0, w, h, r, r)
        filled = int(w * self._v)
        if filled > 2:
            base = QColor(self._accent)
            dark = base.darker(140)
            g = QLinearGradient(0, 0, filled, 0)
            g.setColorAt(0, dark)
            g.setColorAt(1, base)
            p.setBrush(QBrush(g))
            p.drawRoundedRect(0, 0, filled, h, r, r)
            dr = 5
            p.setBrush(base)
            p.drawEllipse(min(filled - dr, w - dr*2), h//2 - dr, dr*2, dr*2)
        p.end()

# ─────────────────────────────────────────────────────────────────────────────
# Кнопка выбора цвета
# ─────────────────────────────────────────────────────────────────────────────
class ColorButton(QPushButton):
    colorChanged = pyqtSignal(str)

    def __init__(self, color: str = "#E91E8C", parent=None):
        super().__init__(parent)
        self.setFixedSize(32, 22)
        self._color = color
        self._update_style()
        self.clicked.connect(self._pick)

    def _update_style(self):
        self.setStyleSheet(
            f"QPushButton {{"
            f"  background: {self._color};"
            f"  border: 1px solid #444;"
            f"  border-radius: 4px;"
            f"}}"
            f"QPushButton:hover {{ border: 1px solid #888; }}"
        )

    def _pick(self):
        c = QColorDialog.getColor(QColor(self._color), self, "Выбор цвета")
        if c.isValid():
            self._color = c.name()
            self._update_style()
            self.colorChanged.emit(self._color)

    def color(self) -> str:
        return self._color

    def set_color(self, c: str):
        self._color = c
        self._update_style()

