class SpotifyOverlay(QWidget):

    def __init__(self):
        super().__init__()
        self._cfg        = load_cfg()
        self.edit_mode   = False
        self._drag_pos      = QPoint()
        self._drag_handle   = None
        self._drag_start_g  = QPoint()
        self._drag_start_geo = None
        self._cover_key  = None
        self._cover_sig  = CoverSignal()
        self._cover_sig.ready.connect(self._on_cover_ready)
        # Для интерполяции времени между poll-ами
        self._last_position_ms  = 0
        self._last_duration_ms  = 0
        self._last_poll_time    = 0.0
        self._is_playing        = False

        self._setup_window()
        self._build_ui()
        self._apply_cfg(initial=True)
        self._setup_tray()

        self._poll_sig = _PollSignal()
        self._poll_sig.updated.connect(self._on_poll_result)
        self._polling = True
        self._poll_thread = threading.Thread(target=self._poll_loop, daemon=True)
        self._poll_thread.start()

        # Таймер только для секунд — каждые 200мс, не блокируется
        self._time_timer = QTimer(self)
        self._time_timer.timeout.connect(self._update_time_display)
        self._time_timer.start(100)   # каждые 100мс — плавно, без нагрузки

        self.show()

    def _setup_window(self):
        self.setWindowFlags(
            Qt.WindowType.FramelessWindowHint |
            Qt.WindowType.WindowStaysOnTopHint |
            Qt.WindowType.Tool
        )
        self.setAttribute(Qt.WidgetAttribute.WA_TranslucentBackground)
        self._hwnd = int(self.winId())
        set_click_through(self._hwnd, True)

    def _build_ui(self):
        self._layout = QHBoxLayout(self)
        self._layout.setContentsMargins(10, 10, 12, 10)
        self._layout.setSpacing(10)

        # Обложка
        self.cover = QLabel()
        self.cover.setAlignment(Qt.AlignmentFlag.AlignCenter)
        self.cover.setStyleSheet("background: #1a1a1a; border-radius: 8px;")
        self._layout.addWidget(self.cover)

        # Правая часть
        right = QVBoxLayout()
        right.setSpacing(1)
        right.setContentsMargins(0, 2, 0, 2)

        self.title_lbl = QLabel("—")
        self.title_lbl.setStyleSheet("color: #FFFFFF;")
        self.title_lbl.setSizePolicy(
            QSizePolicy.Policy.Expanding, QSizePolicy.Policy.Preferred
        )
        right.addWidget(self.title_lbl)

        self.artist_lbl = QLabel("—")
        self.artist_lbl.setSizePolicy(
            QSizePolicy.Policy.Expanding, QSizePolicy.Policy.Preferred
        )
        right.addWidget(self.artist_lbl)

        right.addStretch()

        self.progress = ProgressBar()
        right.addWidget(self.progress)

        time_row = QHBoxLayout()
        time_row.setContentsMargins(0, 3, 0, 0)
        self.time_cur = QLabel("0:00")
        self.time_dur = QLabel("0:00")
        for lbl in (self.time_cur, self.time_dur):
            lbl.setStyleSheet("color: #484848;")
        time_row.addWidget(self.time_cur)
        time_row.addStretch()
        time_row.addWidget(self.time_dur)
        right.addLayout(time_row)

        self._layout.addLayout(right, stretch=1)

    def _apply_cfg(self, initial: bool = False):
        cfg     = self._cfg
        w       = int(cfg.get("width",   360))
        h       = int(cfg.get("height",  100))
        opacity = float(cfg.get("opacity", 0.92))
        accent  = cfg.get("accent", "#E91E8C")
        font_name   = cfg.get("font", "Betm Rounded SemiBold")
        title_sz    = int(cfg.get("font_title_size",  13))
        pos_x   = int(cfg.get("pos_x", -1))
        pos_y   = int(cfg.get("pos_y", -1))

        self.setMinimumSize(160, 50)
        self.setMaximumSize(1200, 400)
        self.resize(w, h)

        # Обложка — квадрат вписанный в высоту минус отступы
        cover_sz = h - 20
        self.cover.setFixedSize(cover_sz, cover_sz)
        self.cover.setStyleSheet("background: #1a1a1a; border-radius: 8px;")
        self._cover_key = None   # сброс → перерисовать

        # Шрифты
        families = QFontDatabase.families()

        def best_font(name: str, size: int, bold: bool = False) -> QFont:
            if name in families:
                f = QFont(name, size)
            else:
                match = next((fam for fam in families if name.lower() in fam.lower()), None)
                f = QFont(match or "Segoe UI", size)
            if bold:
                f.setWeight(QFont.Weight.Bold)
            return f

        # Исполнитель автоматически масштабируется по высоте виджета
        artist_sz_auto = max(7, int(h * 0.105))
        time_sz_auto   = max(9, int(h * 0.075))

        self.title_lbl.setFont(best_font(font_name, title_sz, bold=True))
        self.artist_lbl.setFont(best_font(font_name, artist_sz_auto))
        self.time_cur.setFont(best_font(font_name, time_sz_auto))
        self.time_dur.setFont(best_font(font_name, time_sz_auto))

        # Цвет исполнителя и прогресс-бара
        self.artist_lbl.setStyleSheet(f"color: {accent};")
        self.progress.set_accent(accent)

        # Трей иконка обновляется
        if hasattr(self, "_tray"):
            self._tray.setIcon(make_tray_icon(accent))

        self.setWindowOpacity(opacity)

        if initial and pos_x < 0:
            screen = QApplication.primaryScreen().availableGeometry()
            self.move(screen.right() - w - 20, screen.bottom() - h - 20)
        elif pos_x >= 0:
            self.move(pos_x, pos_y)

        self.update()

    def _update_cover_size(self):
        """Обновляет размер обложки под текущую высоту окна."""
        cover_sz = self.height() - 20
        self.cover.setFixedSize(cover_sz, cover_sz)

    def _setup_tray(self):
        accent = self._cfg.get("accent", "#E91E8C")
        self._tray = QSystemTrayIcon(make_tray_icon(accent), self)
        self._tray.setToolTip("Spotify Overlay")

        menu = QMenu()
        menu.setStyleSheet(
            "QMenu {"
            "  background: #141414; color: #ddd;"
            "  border: 1px solid #222; border-radius: 8px;"
            "  padding: 4px; font-size: 13px;"
            "}"
            "QMenu::item { padding: 6px 14px; border-radius: 5px; }"
            "QMenu::item:selected { background: #E91E8C; color: #fff; }"
            "QMenu::separator { height: 1px; background: #222; margin: 3px 8px; }"
        )

        self._act_move = menu.addAction("⠿  Режим редактирования")
        self._act_move.setCheckable(True)
        self._act_move.triggered.connect(self._toggle_edit)
        menu.addSeparator()
        menu.addAction("↺  Перезагрузить UI").triggered.connect(self._reload_ui)
        menu.addAction("⚙  Настройки").triggered.connect(self._open_settings)
        menu.addSeparator()
        menu.addAction("✕  Выход").triggered.connect(QApplication.quit)

        self._tray.setContextMenu(menu)
        self._tray.activated.connect(self._tray_clicked)
        self._tray.show()

    def _tray_clicked(self, reason):
        if reason == QSystemTrayIcon.ActivationReason.Trigger:
            self.hide() if self.isVisible() else self.show()

    def _reload_ui(self):
        """Перестраивает UI без перезапуска процесса."""
        # Сбрасываем edit mode
        if self.edit_mode:
            self._toggle_edit(False)
            self._act_move.setChecked(False)
        # Удаляем старые виджеты из layout
        while self._layout.count():
            item = self._layout.takeAt(0)
            w = item.widget()
            if w:
                w.deleteLater()
            elif item.layout():
                # вложенные layouts очистить рекурсивно
                sub = item.layout()
                while sub.count():
                    si = sub.takeAt(0)
                    if si.widget():
                        si.widget().deleteLater()
        self._cover_key = None
        self._build_ui()
        self._apply_cfg(initial=False)

    # ─────────────────────────────────────────────────────────────────────
    # Режим редактирования: перемещение + 8 ручек ресайза
    # ─────────────────────────────────────────────────────────────────────
    _HANDLE_SIZE = 10   # пикселей — зона захвата по краям

    # Имена хэндлов и соответствующие курсоры
    _HANDLE_CURSORS = {
        "TL": Qt.CursorShape.SizeFDiagCursor,
        "T":  Qt.CursorShape.SizeVerCursor,
        "TR": Qt.CursorShape.SizeBDiagCursor,
        "L":  Qt.CursorShape.SizeHorCursor,
        "R":  Qt.CursorShape.SizeHorCursor,
        "BL": Qt.CursorShape.SizeBDiagCursor,
        "B":  Qt.CursorShape.SizeVerCursor,
        "BR": Qt.CursorShape.SizeFDiagCursor,
    }

    def _get_handle(self, pos: QPoint):
        """Возвращает имя ручки ('TL','T',…,'BR') или None если центр."""
        hs = self._HANDLE_SIZE
        w, h = self.width(), self.height()
        x, y = pos.x(), pos.y()
        left   = x < hs
        right  = x > w - hs
        top    = y < hs
        bottom = y > h - hs
        mid_h  = not left and not right
        mid_v  = not top  and not bottom
        if top    and left:  return "TL"
        if top    and mid_h: return "T"
        if top    and right: return "TR"
        if mid_v  and left:  return "L"
        if mid_v  and right: return "R"
        if bottom and left:  return "BL"
        if bottom and mid_h: return "B"
        if bottom and right: return "BR"
        return None   # середина → перемещение

    def _toggle_edit(self, checked: bool):
        self.edit_mode = checked
        if checked:
            set_click_through(self._hwnd, False)
            self.setCursor(Qt.CursorShape.SizeAllCursor)
        else:
            self._cfg["pos_x"] = self.x()
            self._cfg["pos_y"] = self.y()
            self._cfg["width"]  = self.width()
            self._cfg["height"] = self.height()
            save_cfg(self._cfg)
            set_click_through(self._hwnd, True)
            self.unsetCursor()
            # Переприменяем шрифты под новый размер
            self._apply_cfg(initial=False)
        self.update()

    def mousePressEvent(self, e):
        if not self.edit_mode or e.button() != Qt.MouseButton.LeftButton:
            return
        gp = e.globalPosition().toPoint()
        handle = self._get_handle(e.position().toPoint())
        self._drag_handle   = handle
        self._drag_start_g  = gp                            # глобальная позиция нажатия
        self._drag_start_geo = self.geometry()              # геометрия до drag'а

    def mouseMoveEvent(self, e):
        if not self.edit_mode:
            return
        pos = e.position().toPoint()
        gp  = e.globalPosition().toPoint()

        # Обновляем курсор даже без зажатой кнопки
        if not (e.buttons() & Qt.MouseButton.LeftButton):
            h = self._get_handle(pos)
            cur = self._HANDLE_CURSORS.get(h, Qt.CursorShape.SizeAllCursor)
            self.setCursor(cur)
            return

        handle = getattr(self, "_drag_handle", None)
        if handle is None and not hasattr(self, "_drag_start_g"):
            return

        dx = gp.x() - self._drag_start_g.x()
        dy = gp.y() - self._drag_start_g.y()
        geo = self._drag_start_geo

        MIN_W, MIN_H = 160, 50

        if handle is None:
            # Перемещение
            self.move(geo.x() + dx, geo.y() + dy)
        else:
            x, y, w, h = geo.x(), geo.y(), geo.width(), geo.height()
            if "L" in handle:
                new_w = max(MIN_W, w - dx)
                x = x + w - new_w
                w = new_w
            if "R" in handle:
                w = max(MIN_W, w + dx)
            if "T" in handle:
                new_h = max(MIN_H, h - dy)
                y = y + h - new_h
                h = new_h
            if "B" in handle:
                h = max(MIN_H, h + dy)
            self.setGeometry(x, y, w, h)
            # Обновляем обложку по новой высоте «на лету»
            cover_sz = h - 20
            self.cover.setFixedSize(cover_sz, cover_sz)

    def mouseReleaseEvent(self, e):
        if self.edit_mode and e.button() == Qt.MouseButton.LeftButton:
            self._drag_handle = None

    def resizeEvent(self, e):
        """Динамически масштабирует обложку при ресайзе через ручки."""
        super().resizeEvent(e)
        if hasattr(self, "cover"):
            sz = self.height() - 20
            self.cover.setFixedSize(sz, sz)

    def _open_settings(self):
        # Singleton — если уже открыто, просто поднимаем на передний план
        if hasattr(self, "_settings_dlg") and self._settings_dlg is not None:
            self._settings_dlg.raise_()
            self._settings_dlg.activateWindow()
            return
        screen = QApplication.primaryScreen().availableGeometry()
        self._settings_dlg = SettingsDialog(
            self._cfg, screen.width(), screen.height(), self.x(), self.y()
        )
        self._settings_dlg.applied.connect(self._on_applied)
        self._settings_dlg.finished.connect(self._on_settings_closed)
        self._settings_dlg.show()

    def _on_settings_closed(self):
        self._settings_dlg = None

    def _on_applied(self, s: dict):
        self._cfg.update(s)
        save_cfg(self._cfg)
        self._apply_cfg()

    def paintEvent(self, _):
        p = QPainter(self)
        p.setRenderHint(QPainter.RenderHint.Antialiasing)
        bg = QColor(28, 28, 28, 232) if self.edit_mode else QColor(16, 16, 16, 210)
        path = QPainterPath()
        path.addRoundedRect(QRectF(self.rect()).adjusted(1, 1, -1, -1), CORNER_R, CORNER_R)
        p.setPen(Qt.PenStyle.NoPen)
        p.setBrush(bg)
        p.drawPath(path)
        if self.edit_mode:
            accent = QColor(self._cfg.get("accent", "#E91E8C"))
            # Рамка
            p.setPen(QPen(QColor(255, 255, 255, 45), 1.5))
            p.setBrush(Qt.BrushStyle.NoBrush)
            p.drawPath(path)
            # 8 ручек-кружков
            w, h = self.width(), self.height()
            cx, cy = w // 2, h // 2
            handle_pts = [
                (0,  0),  (cx, 0),  (w,  0),
                (0, cy),             (w, cy),
                (0,  h),  (cx, h),  (w,  h),
            ]
            hr = 5
            p.setPen(QPen(Qt.GlobalColor.white, 1.5))
            p.setBrush(QBrush(accent))
            for hx, hy in handle_pts:
                p.drawEllipse(QRectF(hx - hr, hy - hr, hr * 2, hr * 2))
        p.end()

    def _poll_loop(self):
        """Бесконечный цикл в фоновом потоке — не блокирует UI."""
        import time as _time
        while self._polling:
            info = get_track_info_sync()
            self._poll_sig.updated.emit(info)
            _time.sleep(POLL_MS / 1000)

    def _on_poll_result(self, info: TrackInfo):
        if not info:
            return

        # Данные трека
        t, a, alb = info.title, info.artist, info.album
        current_key = (t, a, alb)
        
        import time as _time
        now = _time.monotonic()

        # 1. Если трек сменился
        if self._cover_key != current_key:
            self._cover_key = current_key
            self.title_lbl.setText(t if t else "—")
            self.artist_lbl.setText(a if a else "—")
            self.time_dur.setText(ms_to_mmss(info.duration_ms))
            
            self._last_position_ms = info.position_ms
            self._last_duration_ms = info.duration_ms
            self._last_poll_time = now

            def _fetch():
                from media_reader import fetch_cover, get_fallback_cover
                # Передаем альбом для точности поиска
                data = fetch_cover(t, a, alb) 
                if not data:
                    data = get_fallback_cover()
                if data:
                    self._cover_sig.ready.emit(t, a, data)

            threading.Thread(target=_fetch, daemon=True).start()

        # 2. Логика обновления времени (чтобы не было NameError)
        self._is_playing = info.is_playing
        
        # Считаем, где полоска прогресса должна быть по расчетам программы
        elapsed_ms = int((now - self._last_poll_time) * 1000)
        interpolated_pos = self._last_position_ms + elapsed_ms
        
        new_pos = info.position_ms
        
        # Если разница между реальностью и расчетом большая — обновляем базу
        if abs(new_pos - interpolated_pos) > 2000 or new_pos < interpolated_pos:
            self._last_position_ms = new_pos
            self._last_poll_time = now

        self._last_duration_ms = info.duration_ms
        
    def _update_time_display(self):
        """Плавное обновление прогресса каждые 100мс."""
        import time as _time
        if not self._is_playing or self._last_duration_ms <= 0:
            return
        elapsed_ms = int((_time.monotonic() - self._last_poll_time) * 1000)
        pos = min(self._last_position_ms + elapsed_ms, self._last_duration_ms)
        self.time_cur.setText(ms_to_mmss(pos))
        self.progress.set_progress(pos / self._last_duration_ms)

    def _on_cover_ready(self, title: str, artist: str, data: bytes):
        # Ключ теперь тройка (title, artist, album) — сравниваем первые два элемента
        if self._cover_key is None or self._cover_key[:2] != (title, artist):
            return
        cover_sz = self._cfg.get("height", 100) - 20
        px = bytes_to_pixmap(data, cover_sz)
        if px and not px.isNull():
            self.cover.setPixmap(px)
            self.cover.setStyleSheet("border-radius: 8px;")

