    app.setQuitOnLastWindowClosed(False)
    app.setApplicationName("Spotify Overlay")

    if not QSystemTrayIcon.isSystemTrayAvailable():
        print("ОШИБКА: системный трей недоступен")
        sys.exit(1)

    w = SpotifyOverlay()
    sys.exit(app.exec())


